import React, { useState, useEffect } from "react";
import { withRouter } from "react-router-dom";
import styled from "styled-components";

const HeadingLayout = styled.div`
  display: flex;
  flex-direction: row;
  padding: 10px;
  align-items: flex-start;
  justify-content: space-between;
`;

const NavLayout = styled.div`
  width: 200px;
  display: flex;
  flex-direction: column;
`;

const NavSelect = styled.select`
  background-color: white;
  margin: 5px;
`;

const NavButton = styled.button`
  background-color: white;
  margin: 5px;
`;

const ItemLayout = styled.div`
  display: flex;
  background-color: ${props => (props.dark ? "grey" : "#f5f5f5")};
`;

let ListManagementPage = props => {
  const [loading, setLoading] = useState(true);
  const [selectedList, setSelectedList] = useState("");

  const recipes = [{ title: "test1" }, { title: "test2" }, { title: "test3" }];
  const restaurants = [
    { title: "test1" },
    { title: "test2" },
    { title: "test3" }
  ];

  useEffect(() => {
    setLoading(false);
  }, []);

  let restaurantStartAtEven = (recipes.length - 1) % 2 === 0;

  let results = loading ? (
    <p>Loading...</p>
  ) : (
    <div>
      <HeadingLayout>
        <h1>{props.match.params.list}</h1>
        <NavLayout>
          <NavSelect
            onChange={event => setSelectedList(event.target.value)}
            defaultValue=""
          >
            <option value="" hidden="hidden" />
            <option>Favorites</option>
            <option>To Explore</option>
            <option>Do Not Show</option>
          </NavSelect>
          <NavButton
            onClick={() => {
              if (selectedList !== "") {
                props.history.push(`/lists/${selectedList}`);
              }
            }}
          >
            Manage Lists
          </NavButton>
          <NavButton onClick={() => props.history.push("/search")}>
            Return to Results
          </NavButton>
          <NavButton
            onClick={() => {
              props.history.push("/");
            }}
          >
            Return to Search
          </NavButton>
        </NavLayout>
      </HeadingLayout>
      {recipes.map((recipe, idx) => {
        return (
          <ItemLayout key={"recipe" + idx} dark={idx % 2 === 0}>
            <h2>{recipe.title}</h2>
          </ItemLayout>
        );
      })}
      {restaurants.map((restaurant, idx) => {
        let isDark = restaurantStartAtEven ? idx % 2 !== 0 : idx % 2 === 0;
        return (
          <ItemLayout key={"restaurant" + idx} dark={isDark}>
            <h2>{restaurant.title}</h2>
          </ItemLayout>
        );
      })}
    </div>
  );
  return results;
};

export default withRouter(ListManagementPage);
